package arithoprprg;

public class AOHello {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
     int x=10,y=20;
     System.out.println(x+y);
     System.out.println(--x);
     System.out.println(++y);
     System.out.println(x-y);
     System.out.println(--y);
     System.out.println(x++);
     System.out.println(++x);
     System.out.println(y++);
     System.out.println(x--);
     System.out.println(y--);
	}

}
